import React, { Component } from 'react';
import Card from 'react-bootstrap/Card'
import {Button} from "react-bootstrap";
import "./Chevron.css";
import SocketContext from '../socket-context'

class PlayerChevron extends Component {

    handleClick(event) {
        var register = {
            gameHash: 'rtGd7neyPcrejgtn',
            balance: 100,
            status: 'playing',
            seat: 2 }

  }

  componentDidMount() {
    this.props.socket.emit('register', register);

  }




    render() { 
        if(this.props.info=='empty') {
            return ( 
                <Card style={{ width: '18rem' }}>
                    <Card.Body>
                        <Card.Title>
                            <Button onClick={this.handleClick()}>Sit</Button>
                        </Card.Title>
                        <Card.Text>
                        </Card.Text>
                    </Card.Body>
                </Card>

         );
        }

         else {
            return ( 
                <Card style={{ width: '18rem' }}>
                    <Card.Body>
                        <Card.Title>{this.props.info.userid}</Card.Title>
                        <Card.Text>
                            {this.props.info.balance}
                        </Card.Text>
                    </Card.Body>
                    <div className="cards">
                        <div id="card1"><img src={'img/cards/'+this.props.info.card1+".svg"} width='75px' /></div>
                        <div id="card2"><img src={'img/cards/'+this.props.info.card2+".svg"} width='75px' /></div>
                    </div>
                </Card>

         );
        }
    }
}

const PlayerChevronWithSocket = props => (
    <SocketContext.Consumer>
    {socket => <PlayerChevron {...props} socket={socket} />}
    </SocketContext.Consumer>
  )

export default PlayerChevron;
