import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import RegisterModal from './RegisterModal';
import LoginModal from './LoginModal';
import {
  Navbar, 
  NavItem,
  Button,
  Nav
} from 'react-bootstrap';
import FoldButton from './ActionButtons/FoldButton';
import CheckButton from './ActionButtons/CheckButton';
import CallButton from './ActionButtons/CallButton';
import RaiseButton from './ActionButtons/RaiseButton';


class ActionBar extends Component {
  constructor(props) {
    super(props);  
    const buttons = [];
    this.componentDidMount = this.componentDidMount.bind(this);
  }

  componentDidMount() {
    var tempbuttons = [];

    if(this.props.actions) {
      for(var i=0; i<this.props.actions.length;i++) {
        if(this.props.actions[i]=='fold')
         tempbuttons.push(<FoldButton my_id={this.props.my_id} gameid={this.props.gameid}></FoldButton>)
        if(this.props.actions[i]=='check')
         tempbuttons.push(<CheckButton my_id={this.props.my_id} gameid={this.props.gameid}></CheckButton>)
        if(this.props.actions[i]=='call')
         tempbuttons.push(<CallButton my_id={this.props.my_id} gameid={this.props.gameid}></CallButton>)
        if(this.props.actions[i]=='raise')
          tempbuttons.push(<RaiseButton my_id={this.props.my_id} gameid={this.props.gameid}></RaiseButton>)
        }
    }
    this.buttons = tempbuttons;
    console.log(this.buttons);
  }


  loginModalRef = ({handleShow}) => {
    this.showModal = handleShow;
 }

 fold() {
   console.log('fold');
 }


 onLoginClick = () => {
   this.showModal();
 }

 handleClick() {

 }

    render() { 
        return (
        <div>
            <div className="mb-2">
                {this.buttons}
            </div>
        </div> );
    }
}

export default ActionBar;
