import React, { Component } from "react";
import "./Home.css";
import PlayerChevron from "../components/PlayerChevron"



class Table extends Component{
    constructor(props) {
        super(props);
        console.log('ts called');

       // this.state.gameData = {}
       const chevrons = [];
       this.createPlayers(props);
      }
    componentDidMount() {
            //this.setState({gameData:this.props.gameData})
      }


    createPlayers(props) {
        const seats = this.props.gameData.seats;
        const seater = props.jim;
        console.log('seater '+seater)

        this.chevrons = Object.keys(seats).map(function(i) {

            return <PlayerChevron>name={seats[i].userid} balance={seats[i].balance}</PlayerChevron>
        })
}

render () {
  return (
    <div className="Table">
       {this.props.jim}
       {JSON.stringify(this.props.gameData)}
       {this.chevrons}
    </div>
  );
    }
}

export default Table
