import React, { Component } from "react";
import * as serviceWorker from './serviceWorker';
import Routes from "./Routes";
import MyNav from './components/Nav';
import { Link } from "react-router-dom";
import axios from 'axios';
import Table from './containers/Table'
import PlayerChevron from './components/PlayerChevron'
import socketIOClient from "socket.io-client";





class App extends Component {
  constructor() {
    super();
    this.state = {
      logged_in: false,
      my_id: null,
      gameData: {},
      seats: []

    };




  }




  componentDidMount() {

    const socket = socketIOClient('http://127.0.0.1:3000');

    //get id
    socket.emit('handshake', 'give me my id');
    socket.on('handshake', (_id) => {
      //console.log("incoming update " + privateData);
        console.log("My ID is:"+_id);
        this.setState({my_id:_id})
      });

    socket.emit('seatList', 'fart');
    socket.on('publicSeatList', (publicData) => {
      //console.log("incoming update " + privateData);
        const data = JSON.parse(publicData);
        this.setState({gameData:data});  
        this.setState({seats: data.seats});
      });

      socket.on('update', (privateData) => {
        //console.log("incoming update " + privateData);
          const data = JSON.parse(privateData);
          console.log(data);
          this.setState({gameData:data});
          this.setState({seats: data.seats});
        });

  }



  render() {

    return (

      <div className="App container">
        <MyNav>
            <Link to="/">Scratch</Link>
        </MyNav>
        <Table seats={this.state.seats} gameData={this.state.gameData} my_id={this.state.my_id} socket={this.socket} />
        <Routes />
    </div>
    );
  }
}

export default App;
