import React, { Component } from "react";
import * as serviceWorker from './serviceWorker';
import Routes from "./Routes";
import MyNav from './components/Nav';
import { Link } from "react-router-dom";
import axios from 'axios';
import Table from './containers/Table';
import PlayerChevron from './components/PlayerChevron';
import socket from './socket';
import './App.css';




class App extends Component {
  constructor() {
    super();
    this.state = {
      logged_in: false,
      my_id: null,
      gameData: {},
      seats: []

    };




  }




  componentDidMount() {


    //get id
    socket.emit('handshake', 'give me my id');
    socket.on('handshake', (_id) => {
      //console.log("incoming update " + privateData);
        console.log("My ID is:"+_id);
        this.setState({my_id:_id})
      });

      socket.emit('createGame', 'no data');
      socket.on('createGame', (game_id) => {
          console.log("Game ID is:"+game_id);
          this.setState({game_id:game_id})
        });

      id = newgameController.newGame();

    socket.emit('seatList', 'fart');
    socket.emit('startGame','fart');
    socket.on('publicSeatList', (publicData) => {
      //console.log("incoming update " + privateData);
        const data = JSON.parse(publicData);
        this.setState({gameData:data});  
        this.setState({seats: data.seats});
      });

      socket.on('update', (privateData) => {
        //console.log("incoming update " + privateData);
          const data = JSON.parse(privateData);
          console.log(data);
          this.setState({gameData:data});
          this.setState({seats: data.seats});

          if(data.bettingRound.actionOn.hash==this.state.my_id)
            this.setState({actions:data.bettingRound.nextActionsAvailable})
          else
            this.setState({actions:''})
        });

  }



  render() {

    return (

      <div className="App container">
        <MyNav>
            <Link to="/">Scratch</Link>
        </MyNav>
        <Table seats={this.state.seats} 
        gameData={this.state.gameData} 
        my_id={this.state.my_id} 
        actions={this.state.actions} />
        <Routes />
    </div>
    );
  }
}

export default App;
