import React, { Component } from "react";
import "./Home.css";
import PlayerChevron from "../components/PlayerChevron"



class Table extends Component{
    constructor(props) {
        super(props);
        console.log('ts called');

       // this.state.gameData = {}
       const seats = [];
       const chevrons = [];
       this.createPlayers = this.createPlayers.bind(this);
      }

    componentDidMount() {
        this.seats = this.props.gameData.seats;

        this.createPlayers();  

    }
    createPlayers() {

        //const seats = [{userid:'mike',balance:"10"},{userid:'jim',balance:"10"},{userid:'mike',balance:"10"}];
        this.chevrons = Object.keys(this.seats).map(function(i) {
            return <PlayerChevron name={this.seats[i].userid} balance={this.seats[i].balance}></PlayerChevron>
        })
}

render () {
  return (
    <div className="Table">
       {this.props.jim}
       {JSON.stringify(this.props.gameData.seats)}
       {this.chevrons}
    </div>
  );
    }
}

export default Table
