import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import * as serviceWorker from './serviceWorker';
import Routes from "./Routes";
import MyNav from './components/Nav';
import { Link } from "react-router-dom";
import axios from 'axios';
import Table from './containers/Table'
import SocketContext from './socket-context'
import PlayerChevron from './components/PlayerChevron'

class App extends Component {
  constructor() {
    super();
    this.state = {
      endpoint: 'http://127.0.0.1:3000',
      logged_in: false,
      my_id: null,
      gameData: {},
      seats: []
    };

    const socket = socketIOClient(this.state.endpoint);

    socket.emit('seatList', 'rtGd7neyPcrejgtn');

    const App = props => (
      <SocketContext.Provider value={socket}>
        <PlayerChevron />
        <Table />
      </SocketContext.Provider>
    );

    socket.on('publicSeatList', (publicData) => {
      //console.log("incoming update " + privateData);
        const data = JSON.parse(publicData);
        console.log(data);
        this.setState({gameData:data});  
        this.setState({seats: data.seats});
        console.log((socket));
      });

      socket.on('update', (privateData) => {
        //console.log("incoming update " + privateData);
          const data = JSON.parse(privateData);
          console.log(data);
          this.setState({gameData:data});
          this.setState({seats: data.seats});
        });

        var register = {
          gameHash: 'rtGd7neyPcrejgtn',
          balance: 100,
          status: 'playing',
          seat: 2 }

          socket.emit('register', register);


  }




  componentDidMount() {

   // this.getGameData(this);

  }



  render() {

    return (

      <div className="App container">
        <MyNav>
            <Link to="/">Scratch</Link>
        </MyNav>
        <Table seats={this.state.seats} gameData={this.state.gameData} />
        <Routes />
    </div>
    );
  }
}

export default App;
