import React, { Component } from "react";
import "./Home.css";
import PlayerChevron from "../components/PlayerChevron"



class Table extends Component{
    constructor() {
        super();
        this.state = {
            gameData: this.gameData,
            seats: null

        };
        this.createPlayers();
      }

    createPlayers() {
    const players=[];

        for( const [index,value] of this.state.gameData.seats.entries() ){
            for( const [index,value] of this.state.gameData.seats[index].entries()) {
                players.push(<PlayerChevron name={this.state.gameData.seats[index].name} balance={this.gameData.seats[index].balance}></PlayerChevron>);
                }
            };
        return players;
        }
render () {
  return (
    <div className="Table">
        {this.createPlayers()}   
    </div>
  );
    }
}

export default Table
