import React, { Component } from 'react';
import Card from 'react-bootstrap/Card'
import {Button} from "react-bootstrap";
import "./Chevron.css";
import socket from '../socket-context'

class PlayerChevron extends Component {

    handleClick() {
        var register = {
            gameHash: 'OeXYhgHmE4lcX7Ok',
            balance: 100,
            status: 'playing',
            seat: 2 
        }

        this.socket.emit('register', register);
        console.log('click');
  }

  componentDidMount() {


  }




    render() { 
        if(this.props.info=='empty') {
            return ( 
                <Card style={{ width: '18rem' }}>
                    <Card.Body>
                        <Card.Title>
                            <Button onClick={this.handleClick}>Sit</Button>
                        </Card.Title>
                        <Card.Text>
                        </Card.Text>
                    </Card.Body>
                </Card>

         );
        }

         else {
            return ( 
                <Card style={{ width: '18rem' }}>
                    <Card.Body>
                        <Card.Title>{this.props.info.userid}</Card.Title>
                        <Card.Text>
                            {this.props.info.balance}
                        </Card.Text>
                    </Card.Body>
                    <div className="cards">
                        <div id="card1"><img src={'img/cards/'+this.props.info.card1+".svg"} width='75px' /></div>
                        <div id="card2"><img src={'img/cards/'+this.props.info.card2+".svg"} width='75px' /></div>
                    </div>
                </Card>

         );
        }
    }
}


export default PlayerChevron;
