import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import RegisterModal from './RegisterModal';
import LoginModal from './LoginModal';
import {
  Navbar, 
  NavItem,
  Button,
  Nav
} from 'react-bootstrap';

class ActionBar extends Component {
  constructor() {
    super();  

    var buttons = [];
    for(var i=0; i<this.props.actions.length;i++) {
      if(this.props.actions[i]=='fold')
        buttons.push(<Button variant='primary' size='lg' onClick={this.handleClick}>Fold</Button>)
      if(this.props.actions[i]=='check')
        buttons.push(<Button variant='primary' size='lg' onClick={this.handleClick}>Check</Button>)
      if(this.props.actions[i]=='call')
        buttons.push(<Button variant='primary' size='lg' onClick={this.handleClick}>Call</Button>)
      if(this.props.actions[i]=='raise')
        buttons.push(<Button variant='primary' size='lg' onClick={this.handleClick}>Raise</Button>)
      }


  }


  loginModalRef = ({handleShow}) => {
    this.showModal = handleShow;
 }


 onLoginClick = () => {
   this.showModal();
 }

 handleClick(e) {
  e.preventDefault();


 }

    render() { 
        return (
        <div>
            <div className="mb-2">
                {buttons}
            </div>
        </div> );
    }
}

export default ActionBar;
