import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import * as serviceWorker from './serviceWorker';
import Routes from "./Routes";
import MyNav from './components/Nav';
import { Link } from "react-router-dom";
import axios from 'axios';
import Table from './containers/Table'

class App extends Component {
  constructor() {
    super();
    this.state = {
      endpoint: 'http://127.0.0.1:3000',
      logged_in: false,
      my_id: null,

    };

  }




  componentDidMount() {
    console.log(this.getGameData());

  }

  getGameData () {
    const socket = socketIOClient(this.state.endpoint);

    socket.emit('seatList', 'rDyfEeN2a5lZscgY');

    socket.on('publicSeatList', function(privateData) {
      //console.log("incoming update " + privateData);
        const data = JSON.parse(privateData);
        return data;       
      });
  }

  render() {

    return (

      <div className="App container">
        <MyNav>
            <Link to="/">Scratch</Link>
        </MyNav>
        <Table gameData={this.state.gameData} jim={'test'} />
        <Routes />
    </div>
    );
  }
}

export default App;
