import React, { Component } from "react";
import "./Table.css";
import PlayerChevron from "../components/PlayerChevron"
import ActionBar from "../components/ActionBar"
import socket from '../socket'


class Table extends Component{

componentDidMount() {
      var gameid = window.location.pathname.slice(7)
      console.log(gameid)
      socket.emit('seatList', 'fart');
      socket.on('publicSeatList', (publicData) => {
    //console.log("incoming update " + privateData);
      const data = JSON.parse(publicData);
      this.setState({gameData:data});  
      this.setState({seats: data.seats});
    });
}


render () {

  return (
    <div id='Table' className="Table">
        {this.props.seats.map((seat,i) => {
            return <PlayerChevron id={i} info={this.props.seats[i]} gameid={this.props.gameData.gameid} my_id={this.props.my_id}></PlayerChevron>
        })}
      <div id='ActionBar'>
        <ActionBar actions={this.props.actions} my_id={this.props.my_id} gameid={this.props.gameData.gameid}></ActionBar>
      </div> 
    </div>
  );
    }
}

export default Table
